Ext.define('cstt.view.createVersionWindow', {
    extend: 'cstt.view.ui.createVersionWindow',
    alias: 'widget.createVersionWindow',

    initComponent: function() {
        var me = this;
        me.callParent(arguments);
    },

    onCancelClick: function(button, e, options) {
        this.close();
    },

    onSaveClick: function(button, e, options) {
        // Formular체berpr체fung
        if (this.down('form').getForm().isValid()) {
            // Wait Message anzeigen	
            Ext.MessageBox.wait('Speichern ...', 'Bitte warten ...');

            this.down('#creator_ID').setValue(sessionUserID);

            Ext.Ajax.request({
                waitMsg: 'Bitte warten...',
                method: 'POST',
                url: '../../',
                scope: this,
                // Header auf application/json setzen - CouchDB
                headers: {
                    'Content-Type': 'application/json; charset=utf-8'
                },
                // Die Formular-Eingaben uebergeben
                params: Ext.JSON.encode(this.down('form').getForm().getValues()),
                success: function(){
                    // Fenster schliessen
                    this.close();

                    // Wait Message verbergen			
                    Ext.MessageBox.hide();


                    if(Ext.getCmp('viewport').getComponent('centerTabPanel').getComponent('overviewVersionGrid')) {
                        Ext.data.StoreManager.lookup('versionManagementStore').load();				
                    }	


                },
                failure: function(){				
                    // Wait Message verbergen			
                    Ext.MessageBox.hide();

                    // Zeige Fehler w채hrend des Speicherns			
                    boxErrorDuringSaving();			  
                }
            });
        }
        else {		
            // Zeige Warnung - nicht alle Felder ausgef체llt
            boxWarningRequiredFields();	
        }
    }

});